<?php	// Контроллер	class JournalController extends Controller	{		public $defaultAction = "StudentJournal";				public static $allowed_users = [Student::USER_TYPE];				// Папка вьюх		protected $_viewsFolder	= "journal";				public function beforeAction()		{			$this->addJs("ng-journal-app");		}				public function actionStudentJournal()		{				$Student = Student::findById(User::fromSession()->id_entity);						# Генерируем данные для ангуляра			$ang_init_data = angInit([				"Journal"		=> $Student->getVisits(true),				"Groups"		=> $Student->getGroups(true),			]);			$this->setTabTitle("Журнал посещаемости");			$this->render("student_journal", [				"ang_init_data" => $ang_init_data			]);		}			}