<?php	// Контроллер	class MetroController extends Controller	{				public function actionGetDistance()		{			extract($_GET);						$Metors = Metro::getCached();						foreach ($Metors as $Metro) {				$Metro = (object)$Metro;				$distance = Metro::getDistance($Metro->lat, $Metro->lng, $lat, $lng);								$return[] = [$Metro->title, $distance];			}						$d = [];			foreach ($return as $id => $row) {				$d[$id] = $row[1];			}			array_multisort($d, SORT_ASC, $return);						returnJson($return);		}				##################################################		###################### AJAX ######################		##################################################			}