<?php	// Контроллер	class NotificationController extends Controller	{		public $defaultAction = "list";		// Папка вьюх		protected $_viewsFolder	= "notification";		/**		 * Список уведомлений.		 *		 */		public function actionList()		{			$this->setTabTitle("Напоминания");			$this->addJs("ng-notification-app");			// Получаем напоминания в нужной сортировке			$Notifications = Notification::findAll([				"order"	=> "date DESC, time DESC"			]);			// Данные для ангуляра			$ang_init_data = angInit([				"notifications" 	=> $Notifications,				"noitfication_types"=> NotificationTypes::$all,			]);			$this->render("list", [				"ang_init_data" => $ang_init_data			]);		}		##################################################		###################### AJAX ######################		##################################################	}