<?php	// Контроллер	class NotificationController extends Controller	{		public $defaultAction = "list";				// Папка вьюх		protected $_viewsFolder	= "notification";										/**		 * Список уведомлений.		 * 		 */		public function actionList()		{			$this->setTabTitle("Напоминания");			$this->addJs("ng-notification-app");						$Notifications = Notification::findAll([				"order"	=> "date DESC, time DESC"			]);						$this->render("list", [				"Notifications" => $Notifications			]);		}				##################################################		###################### AJAX ######################		##################################################							}