<?php	// Контроллер	class RatingController extends Controller	{		public $defaultAction = "rating";				// Папка вьюх		protected $_viewsFolder	= "rating";				public function actionRating()		{			$this->setTabTitle("Рейтинг филиалов");									$Students = Student::findAll([				"condition" => "branches != ''"			]);						foreach ($Students as &$Student) {				$Student->Contract = $Student->getLastContract();				foreach ($Student->branches as $id_branch) {					$rating[$id_branch]++;					if ($Student->Contract) {						$rating[$id_branch] += count($Student->Contract->subjects); 					}				}			}						asort($rating);			$rating = array_reverse($rating, true);						$this->render("rating", [				"rating" => $rating			]);		}				##################################################		###################### AJAX ######################		##################################################							}