<?php	// Контроллер	class SmsController extends Controller	{		public $defaultAction = "history";				// Папка вьюх		protected $_viewsFolder	= "sms";				public function beforeAction()		{			$this->addJs("ng-sms-app");		}				// Страница входа			public function actionHistory()		{				$this->setTabTitle("История СМС");			$search_fields = [				'search' => $_GET['search'],				'phone' => $_GET['phone']			];			$History = SMS::getByPage($_GET["page"], $search_fields);						$ang_init_data = angInit([				'currentPage' => $_GET['page'],				'search'	  => $_GET['search'],				'phone' 	  => cleanNumberForSearch($_GET['phone'])			]);									$this->render("history", [				"History" => $History,				"ang_init_data" => $ang_init_data,			]);		}			}