<?php	// Контроллер	class SmsController extends Controller	{		public $defaultAction = "history";				// Папка вьюх		protected $_viewsFolder	= "sms";				// Страница входа			public function actionHistory()		{				$this->setTabTitle("История СМС");						$History = SMS::findAll([				"order" => "id DESC",			]);						$this->render("history", [				"History" => $History,			]);		}			}