<?php	// Контроллер	class SmsController extends Controller	{		public $defaultAction = "history";				// Папка вьюх		protected $_viewsFolder	= "sms";				public function beforeAction()		{			$this->addJs("ng-sms-app");		}				// Страница входа			public function actionHistory()		{				$this->setTabTitle("История СМС");						$History = SMS::getByPage($_GET["page"]);						$ang_init_data = angInit([				"currentPage" => $_GET["page"],			]);									$this->render("history", [				"History" => $History,				"ang_init_data" => $ang_init_data,			]);		}			}