<?php	// Контроллер	class StudentsController extends Controller	{		public $defaultAction = "findById";				// Папка вьюх		protected $_viewsFolder	= "students";						/**		 * Перенаправить на заявку студента по ID.		 * 		 */		public function actionFindById()		{			$id_student = $_GET["id_student"];						$Request = Request::findByStudent($id_student);									$this->redirect("requests/edit/" . $Request->id, true);		}				##################################################		###################### AJAX ######################		##################################################							}