<?php	// Контроллер	class StudentsController extends Controller	{		public $defaultAction = "findById";				// Папка вьюх		protected $_viewsFolder	= "request";						/**		 * Перенаправить на заявку студента по ID.		 * 		 */		public function actionFindById()		{			$this->addJs("ng-request-app");			$id_student = $_GET["id_student"];					$Request = Request::findByStudent($id_student);			$_GET["id"] = $Request->id;			RequestController::actionEdit($id_student);// 			$this->redirect("requests/edit/" . $Request->id, true);		}				##################################################		###################### AJAX ######################		##################################################							}